import ReactDOM from 'react-dom';
import './index.css';
import React, { Component } from 'react';
import Item from './Item';

class ItemParent extends Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      numTries: 0,
      sucTries: 0,
      overlapsBin: false,
    };
  }
  setOverlapsBin() {
    this.setState({ overlapsBin: true, });
  }

  incNumTries() {
    const prevTries = this.state.numTries;
    const newState = {
      numTries: prevTries + 1
    };
    this.setState(newState);
    console.log("numTries: " + this.state.numTries);
  }

  incSucTries() {
    const prevTries = this.state.sucTries;
    const newState = {
      sucTries: prevTries + 1
    };
    this.setState(newState);
    console.log("sucTries: " + this.state.sucTries);
  }

  render() {
    const itemData = [['recycling', 'PlasticCup']];
    const items = itemData.map((v, i) =>
      <Item
        incNumTries={this.incNumTries.bind(this)}
        incSucTries={this.incSucTries.bind(this)}
        setOverlapsBin={this.setOverlapsBin.bind(this)}
        bin={v[0]}
        itemnumber={i}
        itemtype={v[1]} />
    )


    // bin='recycling' itemtype='PlasticCup' itemnumber='0' />, document.getElementById('item0'));
    // bin='compost' itemtype='Burger' itemnumber='1' />, document.getElementById('item1'));
    // bin='landfill' itemtype='Pencil' itemnumber='2' />, document.getElementById('item2'));
    // bin='recycling' itemtype='ScratchPaper' itemnumber='3' />, document.getElementById('item3'));
    // bin='recycling' itemtype='SodaCan' itemnumber='4' />, document.getElementById('item4'));
    // bin='compost' itemtype='Pizza' itemnumber='5' />, document.getElementById('item5'));
    // bin='recycling' itemtype='Flyer' itemnumber='6' />, document.getElementById('item6'));
    // bin='landfill' itemtype='Styrofoam' itemnumber='7' />, document.getElementById('item7'));
    // bin='compost' itemtype='PopcornBox' itemnumber='8' />, document.getElementById('item8'));
    // bin='compost' itemtype='Napkin' itemnumber='9' />, document.getElementById('item9'));
    // bin='landfill' itemtype='RubberBand' itemnumber='10' />, document.getElementById('item10'));
    // bin='landfill' itemtype='ChipBag' itemnumber='11' />, document.getElementById('item11'));



    // let item;
    // if (this.state.overlapsBin == false) {
    //   item = <Item incNumTries={this.incNumTries.bind(this)} incSucTries={this.incSucTries.bind(this)} setOverlapsBin={this.setOverlapsBin.bind(this)} bin={this.props.bin} itemnumber={this.props.itemnumber} itemtype={this.props.itemtype} />;
    // }

    return (
      <div>
        {items}
      </div>
    );
  }
}


export default ItemParent;
