import './index.css';
import React, { Component } from 'react';
import Item from './Item';

class ItemParent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numTries: 0,
      sucTries: 0,
      overlapsBin: false,
      itemNumTries: 0
    };
  }
  setOverlapsBin() {
    this.setState({ overlapsBin: true });
  }

  incNumTries() {
    this.setState({ numTries: this.state.numTries + 1 });
    this.setState({ itemNumTries: this.state.itemNumTries + 1 });
  }

  incSucTries() {
    this.setState({ sucTries: this.state.sucTries + 1 });

  }

  render() {
    const itemData = [
      ['recycling', 'PlasticCup'],
      ['compost', 'Burger'],
      ['landfill', 'Pencil'],
      ['recycling', 'ScratchPaper'],
      ['recycling', 'SodaCan'],
      ['compost', 'Pizza'],
      ['recycling', 'Flyer'],
      ['landfill', 'Styrofoam'],
      ['compost', 'PopcornBox'],
      ['compost', 'Napkin'],
      ['landfill', 'RubberBand'],
      ['landfill', 'ChipBag']];
    const items = itemData.map((v, i) =>
      <Item
        incNumTries={this.incNumTries.bind(this)}
        incSucTries={this.incSucTries.bind(this)}
        setOverlapsBin={this.setOverlapsBin.bind(this)}
        bin={v[0]}
        itemnumber={i}
        itemtype={v[1]} />
    )
    // let item;
    // if (this.state.overlapsBin == false) {
    //   item = <Item incNumTries={this.incNumTries.bind(this)} incSucTries={this.incSucTries.bind(this)} setOverlapsBin={this.setOverlapsBin.bind(this)} bin={this.props.bin} itemnumber={this.props.itemnumber} itemtype={this.props.itemtype} />;
    // }

    return (
      <div>
        <h1 className="score">Total Score: {this.state.sucTries} / {this.state.numTries}</h1>
        <h1 className="score">Tries for Current Item: {this.props.itemNumTries}</h1>
        {items}
      </div >
    );
  }
}

export default ItemParent;
