import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import Bin from './Bin';
import Item from './Item';
import * as serviceWorker from './serviceWorker';

function dw_getPageOffsets(el) {
    var sOff = dw_getScrollOffsets(), left = 0, top = 0, props;

    if ( el.getBoundingClientRect ) {
        props = el.getBoundingClientRect();
        left = props.left + sOff.x;
        top = props.top + sOff.y;
    } else { // for older browsers
        do {
            left += el.offsetLeft;
            top += el.offsetTop;
        } while ( (el = el.offsetParent) );
    }
    return { x: Math.round(left), y: Math.round(top) };
}
var el = document.getElementById('display');
var loc = dw_getPageOffsets(el);
alert(loc.left);
function dw_getScrollOffsets() {
    var doc = document, w = window;
    var x, y, docEl;

    if ( typeof w.pageYOffset === 'number' ) {
        x = w.pageXOffset;
        y = w.pageYOffset;
    } else {
        docEl = (doc.compatMode && doc.compatMode === 'CSS1Compat')?
                doc.documentElement: doc.body;
        x = docEl.scrollLeft;
        y = docEl.scrollTop;
    }
    return {x:x, y:y};
}

ReactDOM.render(<Item />, document.getElementById('root'));
ReactDOM.render(<Bin />, document.getElementById('bin1'));
ReactDOM.render(<Bin />, document.getElementById('bin2'));
ReactDOM.render(<Bin />, document.getElementById('bin3'));
