import React, { Component } from 'react';
import Draggable from 'react-draggable';
// import PlasticCup from './itemimages/PlasticCup.jpg';
// import Burger from './itemimages/Burger.jpg';
// import Pencil from './itemimages/Pencil.jpg';
// import ScratchPaper from './itemimages/ScratchPaper.jpg';
// import Flyer from './itemimages/Flyer.jpg';
// import SodaCan from './itemimages/SodaCan.jpg';
// import Pizza from './itemimages/Pizza.jpg';
// import Napkin from './itemimages/Napkin.jpg';
// import PopcornBox from './itemimages/PopcornBox.jpg';
// import RubberBand from './itemimages/RubberBand.jpg';
// import Styrofoam from './itemimages/Styrofoam.jpg';
// import Floss from './itemimages/Floss.jpg';
// import Newspaper from './itemimages/Newspaper.jpg';
// import Flower from './itemimages/Flower.jpg';
// import ChipBag from './itemimages/ChipBag.jpg';

class Item extends Component {
  constructor(props) {
    super(props);
    this.state = {
      itemNumTries: 0,
    };
  }

  changeBinColor(binID, color) {
    document.getElementById(binID).style.borderColor = color;
  }

  handleStop = (e, data) => {
    this.props.incNumTries();
    this.setState({ itemumTries: this.state.itemnumTries + 1 });
    let bin = `${this.props.bin}bin`;
    let itemnumber = `item${this.props.itemnumber}`;
    let rect1 = document.getElementById(itemnumber).getBoundingClientRect();
    let rect2 = document.getElementById(bin).getBoundingClientRect();
    if (rect1.right > rect2.left && rect2.top < rect1.bottom && rect1.bottom > rect2.top && rect1.left < rect2.right) {
      this.props.incSucTries();
      if (this.props.bin === 'recycling') {
        this.changeBinColor('recyclingbin', 'green');
        this.changeBinColor('compostbin', 'white');
        this.changeBinColor('landfillbin', 'white');
      }
      else if (this.props.bin === 'compost') {
        this.changeBinColor('compostbin', 'green');
        this.changeBinColor('recyclingbin', 'white');
        this.changeBinColor('landfillbin', 'white');
      }
      else if (this.props.bin === 'landfill') {
        this.changeBinColor('landfillbin', 'green');
        this.changeBinColor('compostbin', 'white');
        this.changeBinColor('recyclingbin', 'white');
      }
    }
    else {
      this.changeBinColor('landfillbin', 'red');
      this.changeBinColor('compostbin', 'red');
      this.changeBinColor('recyclingbin', 'red');
    }
    /*console.log("x: " + data.x);
console.log("y: " + data.y); */
    this.props.setOverlapsBin();
  }
  handleDrag = (e, data) => {
    const { x, y } = data;
    this.setState({
      position: { x: x, y: y }
    });
    this.changeBinColor('landfillbin', 'white');
    this.changeBinColor('compostbin', 'white');
    this.changeBinColor('recyclingbin', 'white');
  }

  eventLogger = (e: MouseEvent, data: Object) => {
    console.log('Event: ', e);
    console.log('Data', data);
  }

  getImage = () => {
    // let x = this.props.itemtype;
    return require(`./itemimages/${this.props.itemtype}.png`);
    // if (x === 'PlasticCup')
    //   return PlasticCup;
    // else if (x === 'Burger')
    //   return Burger;
    // else if (x === 'Pencil')
    //   return Pencil;
    // else if (x === 'ScratchPaper')
    //   return ScratchPaper;
    // else if (x === 'Flyer')
    //   return Flyer;
    // else if (x === 'SodaCan')
    //   return SodaCan;
    // else if (x === 'Pizza')
    //   return Pizza;
    // else if (x === 'PopcornBox')
    //   return PopcornBox;
    // else if (x === 'Napkin')
    //   return Napkin;
    // else if (x === 'RubberBand')
    //   return RubberBand;
    // else if (x === 'Styrofoam')
    //   return Styrofoam;
    // else if (x === 'Flower')
    //   return Flower;
    // else if (x === 'Newspaper')
    //   return Newspaper;
    // else if (x === 'Floss')
    //   return Floss;
    // else if (x == 'ChipBag')
    //   return ChipBag;
  }
  render() {
    let image = this.getImage();

    return (
      <Draggable
        axis="both"
        handle=".item"
        defaultPosition={null}
        position={this.state.position}
        scale={1}
        onStart={this.handleStart}
        onDrag={this.handleDrag}
        onStop={this.handleStop}>
        <div className="item" id={`item${this.props.itemnumber}`}>
          <p>{this.props.itemtype}</p>
          <img className="pic" draggable="false" src={image} alt={this.props.itemtype} />
        </div>
      </Draggable>
    );
  }
}
export default Item;
