import React, { Component } from 'react';
import ReactDOM from 'react-dom'
import Draggable from 'react-draggable';
import plasticcup from './itemimages/plasticcup.jpg';

class Item extends Component {
    constructor(props, context) {
        super(props, context);
        this.state = { 
            position: {
                x: 0, y: 0
            }
        };
    }

    handleStop = (e, data) => {
          if (this.props.bin == 'recycling')
            {
              down = -60; 
              left = 96; 
              up = 400; right = 259
            }
            else
            {
              down: 500; left: 96; up: 400; right: 259
            }
        if (data.x > down && data.y > left && data.y < up && data.x < right) {
           this.setState({
               position: {x: -999, y: -999}
           })
        }
        console.log(data.x);
        console.log(data.y);
    }
    handleDrag = (e, data) => {
        const {x, y} = data;
        this.setState({
            position: {x:x, y:y}
        })
    }
    eventLogger = (e: MouseEvent, data: Object) => {
        console.log('Event: ', e);
        console.log('Data', data);
    }
  render() {
    return (
        <Draggable
        axis = "both"
        handle = ".item"
        defaultPosition={null}
        position={this.state.position}
        scale={1}
        onStart={this.handleStart}
        onDrag={this.handleDrag}
        onStop={this.handleStop}>
        <div className = "item">
            <img id="layout" draggable="false" src={plasticcup} />
            <p>{this.props.bin}</p>
        </div>
        </Draggable>
    );
  }
}
export default Item;
